insert into TLog_Dealer_Switch(Id,Abz,Aczr,Dczsj,Ajxsdm,Aqhlx)
(select sys_guid(),'系统自动切换（影像件）','admin',sysdate,dealer.aid,'1'
from tBase_Dealer dealer  inner join dealer_switch_temp temp on temp.ajxsdm = dealer.aid)

